INSERT INTO "ability" VALUES(115,'Transcedence','','Zenyatta enters a state of heightened existence for a short period of time. While transcendent, Zenyatta cannot use abilities or weapons, but is immune to damage and automatically restores his health and that of nearby allies.','10 meters','','11 m/s','','Ultimate','FALSE','6 seconds','','300 per second',23);
INSERT INTO "ability" VALUES(116,'Pulse Pistols','1 second reload','Tracer rapid-fires both of her pistols.','11 to 30 meters falloff','3 - 12 per shot','20 shots per second','40','Primary Fire','TRUE','','','',7);
INSERT INTO "ability" VALUES(117,'Blink','3 seconds','Tracer zips horizontally through space in the direction she’s moving, and reappears several yards away. She stores up to three charges of the blink ability and generates more every few seconds.','7 meters','','','3 charges','Ability','FALSE','','','',7);
INSERT INTO "ability" VALUES(118,'Recall','12 seconds','Tracer bounds backward in time, returning her health, ammo and position on the map to precisely where they were a few seconds before.','','','','','Ability','FALSE','1.25 seconds. Rewinds 3 seconds back','','Reverts to health 3 seconds prior.',7);
